import View360 from "../View360";
import Projection, { ProjectionOptions } from "../projection/Projection";
export interface ReadyEvent {
    type: string;
    target: View360;
}
export interface LoadStartEvent {
    type: string;
    target: View360;
    src: ProjectionOptions["src"];
    video: ProjectionOptions["video"];
}
export interface LoadEvent {
    type: string;
    target: View360;
    src: ProjectionOptions["src"];
    video: ProjectionOptions["video"];
}
export interface ProjectionChangeEvent {
    type: string;
    target: View360;
    projection: Projection;
}
export interface ResizeEvent {
    type: string;
    target: View360;
    width: number;
    height: number;
}
export interface BeforeRenderEvent {
    type: string;
    target: View360;
}
export interface RenderEvent {
    type: string;
    target: View360;
}
export interface InputStartEvent {
    type: string;
    target: View360;
    srcEvent: MouseEvent | TouchEvent | KeyboardEvent;
    inputType: "rotate" | "zoom";
    isTouch: boolean;
    isKeyboard: boolean;
}
export interface InputEndEvent {
    type: string;
    target: View360;
    inputType: "rotate" | "zoom";
    isTouch: boolean;
    isKeyboard: boolean;
    scrolling: boolean;
}
export interface ViewChangeEvent {
    type: string;
    target: View360;
    yaw: number;
    pitch: number;
    zoom: number;
    quaternion: number[];
}
export interface StaticClickEvent {
    type: string;
    target: View360;
    isTouch: boolean;
}
export interface VRStartEvent {
    type: string;
    target: View360;
}
export interface VREndEvent {
    type: string;
    target: View360;
}
